<script src="<?php echo base_url(); ?>assets/admin/js/plugin/datatables/jquery.dataTables.min.js"></script><script src="<?php echo base_url(); ?>assets/admin/js/plugin/datatables/dataTables.bootstrap.min.js"></script><div id="main" role="main">	<?php $this->load->view('header/breadcrumb'); ?>	<div id="content">		<div class="m-b-10">			<div class="pull-left">				<h3 class="pull-left">					<strong>Registered Users</strong>				</h3>			</div>		</div>		<section class="" id="widget-grid">			<!-- row -->			<div class="row">				<article class="col-xs-12 col-sm-12 col-md-12 col-lg-12 sortable-grid ui-sortable">					<div data-widget-editbutton="false" id="wid-id-0" class="jarviswidget jarviswidget-color-darken" role="widget">						<header role="heading">							<h2><?= $list_heading ?></h2>						</header>						<div role="content">							<div class="widget-body no-padding">								<div id="dt_basic_wrapper" class="dataTables_wrapper form-inline no-footer">									<table width="100%" class="table table-striped table-bordered table-hover dataTable no-footer" id="list" role="grid">										<thead>											<tr role="row">												<th>Name</th>												<th>Email</th>												<th>Contact</th>												<th>Gender</th>												<th>Source</th>												<th>Status</th>												<th>Action</th>											</tr>										</thead>										<tbody>											<?php												if (!empty($registered_user)) {												    foreach ($registered_user as $user) {												        if(!empty($user->oauth_provider)){												        }												        ?>											<tr role="row" class="even">												<td class=""><?php echo!empty($user->name) ? $user->name : '' ?></td>												<td class=""><?php echo!empty($user->email) ? '<strong>'.$user->email.'</strong>' : '-' ?></td>												<td class=""><?php echo!empty($user->contact) ? $user->contact : '-' ?></td>												<td class=""><?php echo!empty($user->gender) ? $user->gender : '-' ?></td>												<td class="">