res) {	                var data = $.parseJSON(res);	                if (data.response === 'true') {	                    location.reload();	                } else {	                    alert('User not deleted! Something went wrong.')	                }	            }	        })	    });		    $('#confirm-delete').on('show.bs.modal', function (e) {	        var data = $(e.relatedTarget).data();	        $('.title', this).text(data.recordTitle);	        $('.btn-ok', this).data('recordId', data.recordId);	    });	});</script>